import React, { useState, useEffect } from "react";

function EditPost(props) {
    // On récupère les données utiles
    const { post_id, author_id, post_title, post_content, post_img } = props;

    // On prépare le state local
    const [title, setTitle] = useState(post_title);
    const [content, setContent] = useState(post_content);
    const [image, setImage] = useState(post_img);

    // On récupère les données pour les authorisations
    const [isAuthor, setIsAuthor] = useState(false);
    const user = JSON.parse(sessionStorage.getItem("isAuthenticate"));
    const token = user.pass;
    const user_id = user.id;

    // On créer le formData
    const formData = new FormData();
    formData.append("title", title);
    formData.append("content", content);
    // formData.append("image", image);

    // On configure la requête
    const url = `http://localhost:8080/api/articles/${post_id}`;
    const reqOptions = {
        method: "PUT",
        body: formData,
        headers: {
            Authorization: `Bearer ${token}`,
        },
    };

    const set_author = () => {
        if (user_id === author_id) {
            setIsAuthor(true);
        }
    };

    useEffect(() => {
        set_author();
    }, []);

    //   const toggleEdit = () => {
    //     if (!editMod) {
    //         setEditMod(true);
    //         console.log("mode edition activée !");
    //     } else {
    //         setEditMod(false);
    //         console.log("mode edition désactivée !");
    //     }
    // };

    // Fonction pour modifier un commentaire
    const editPost = async (e) => {
        e.preventDefault();
        try {
            let res = await fetch(url, reqOptions);
            let editRes = await res.json();

            console.log("=====> Réponse commentaire envoyé : ");
            console.log(editRes);

            window.location.reload();
        } catch (err) {
            console.log(err);
        }
    };

    function handleChange(e) {
        setImage(e.target.files[0]);
    }

    return (
        <div>
            <form onSubmit={editPost} className="post__edit" method="PUT" encType="multipart/form-data">
                <label>
                    <input
                        className="newPost__add--title"
                        type="text"
                        name="title"
                        value={title}
                        placeholder="Votre titre ici..."
                        onChange={(e) => setTitle(e.target.value)}
                    />
                </label>

                <label>
                    <textarea
                        className="newPost__add--content"
                        type="text"
                        name="content"
                        value={content}
                        placeholder="Votre text ici...."
                        onChange={(e) => setContent(e.target.value)}
                    />
                </label>

                <img src={image} />

                <div className="newPost__btn">
                    {!image ? (
                        <input className="btn" type="file" name="image" onChange={handleChange} />
                    ) : (
                        <input className="btn" type="file" name="image" src={image} onChange={handleChange} />
                    )}
                    {/* <input className="btn" type="file" name="image" src={image} onChange={handleChange} /> */}
                    <input className="newPost__add--send btn" type="submit" value="Envoyer" />
                </div>
            </form>
        </div>
    );
}

export default EditPost;
