import { Route, Navigate } from "react-router-dom";

// Reception de 3 props : cgildren, isAuthenticated, ...rest
function PublicRoute({ children, isAuthenticated, ...rest }) {
    return (
        <Route
            {...rest}
            render={({ location }) =>
                !isAuthenticated ? children : <Navigate to={{ pathname: "/home", state: { from: location } }} />
            }
        />
    );
}

export default PublicRoute;
