import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

function Profile() {
    let { userSlug } = useParams();
    const [userData, setUserData] = useState({ profile: {} });
    const [dataIsLoaded, setDataIsLoaded] = useState(false);

    const [isAuthorized, setIsAuthorized] = useState(false);

    const user = JSON.parse(sessionStorage.getItem("isAuthenticate"));
    const token = user.pass;
    const user_id = user.id;
    const user_role = user.role;

    // Request options
    const url = `http://localhost:8080/api/profiles/${userSlug}`;
    const reqOptions = {
        method: "GET",
        headers: {
            Authorization: `Bearer ${token}`,
            Accept: "application/json",
            "Content-Type": "application/json",
        },
    };

    const fetchProfile = () => {
        fetch(url, reqOptions)
            .then((res) => {
                if (res.ok) {
                    return res.json();
                    // console.log(res);
                } else {
                    console.log("Ce profile n'existe pas");
                }
            })
            .then((data) => {
                setUserData(data);
                setDataIsLoaded(true);

                console.log(data);
            })
            .catch((err) => {
                console.log(err);
            });

        // try {
        //     const res = await fetch(url, reqOptions);
        //     const data = await res.json();
        //     // console.log(userData);

        //     if (!res.ok) {
        //         console.log("Ce profile n'existe pas");
        //     } else {
        //         setUserData(data);
        //         setDataIsLoaded(true);
        //     }
        // } catch (err) {
        //     console.log(err);
        // }
    };

    useEffect(() => {
        fetchProfile();
    }, []);

    if (!dataIsLoaded) {
        return (
            <div>
                <h1> Pleases wait some time.... </h1>
            </div>
        );
    }

    console.log("", userData);

    return (
        <div className="profile">
            <div className="container">
                <h1>{userData.profile.firstName + " " + userData.profile.lastName}</h1>
                {!userData.profile.avatarUrl ? (
                    <div className="author__img initiales">
                        <p>
                            {`${userData.profile.firstName} ${userData.profile.lastName}
                                                `
                                .match(/\b\w/g)
                                .join("")
                                .toUpperCase()}
                        </p>
                    </div>
                ) : (
                    <img
                        className="author__img"
                        src={userData.profile.avatarUrl}
                        alt={"Photo de profile de " + userData.profile.firstName + " " + userData.profile.lastName}
                    />
                )}

                <div>{userData.profile.inscrit_le}</div>

                <div>{userData.profile.birthdate}</div>

                <div>{userData.profile.role}</div>

                <div>{userData.profile.email}</div>

                <div>{userData.profile.password}</div>

                <div className="profile__btn">
                    <button className="btn">Edit</button>
                    <button className="btn">Supprimer</button>
                </div>
            </div>
        </div>
    );
}

export default Profile;
