import React, { useState } from "react";

function EditComment(props) {
    // On récupère l'id de l'utilisateur avec props
    const { post_id, comment_id, comment_value } = props;

    // On prépare le state local pour stocker les données à modifier
    const [modification, setModification] = useState(null);

    // On récupère les données de connexion de l'utilisateur loggé
    const user = JSON.parse(sessionStorage.getItem("isAuthenticate"));
    const token = user.pass;

    // On créer un objet a envoyer pour la modification
    const commentObject = {
        comment: modification,
    };

    const url = `http://localhost:8080/api/articles/${post_id}/comments`;
    const reqOptions = {
        method: "POST",
        body: JSON.stringify(commentObject),
        headers: {
            Authorization: `Bearer ${token}`,
            Accept: "application/json",
            "Content-Type": "application/json",
        },
    };

    return (
        <div className="comments__editForm">
            <h1>Ajouter un commentaire</h1>
            <form comment_editForm--form onSubmit="">
                <label>
                    <textarea
                        type="text"
                        name="content"
                        placeholder="Ajouter un nouveau commentaire"
                        value={comment_value}
                        onChange={(e) => setModification(e.target.value)}
                    />
                </label>

                <div>
                    <input className="comment__send--btn btn" type="submit" value="Envoyer" />
                </div>
            </form>
        </div>
    );
}

export default EditComment;
