import React, { useState, useEffect } from "react";

function Comments(props) {
    const { post_id } = props;
    const [msg, setMsg] = useState("");
    const [comments, setComments] = useState([]);
    const [dataIsLoaded, setDataIsLoaded] = useState(false);

    // Token de l'utilisateur
    const user = JSON.parse(sessionStorage.getItem("isAuthenticate"));
    const token = user.pass;
    const user_id = user.id;

    // Request Options
    const url = `http://localhost:8080/api/articles/${post_id}/comments`;
    const reqOptions = {
        method: "GET",
        headers: {
            Authorization: `Bearer ${token}`,
            Accept: "application/json",
            "Content-Type": "application/json",
        },
    };

    const fetchComments = () => {
        fetch(url, reqOptions)
            .then((res) => {
                if (res.ok) {
                    return res.json();
                } else {
                    setMsg("Soyez le premier a ajouter un commentaire !");
                    console.log("pas de commentaires à afficher");
                }
            })
            .then((data) => {
                // console.log(data);
                setComments(data);
                setDataIsLoaded(true);
            })
            .catch((err) => {
                console.log(err);
            });
    };

    useEffect(() => {
        fetchComments();
    }, []);

    console.log(comments);

    const showComments = comments.allComments.map((comment) => {
        return <div className="comment" key={comment.id}></div>;
    });

    if (!dataIsLoaded)
        return (
            <div>
                <h1> {msg} </h1>
            </div>
        );

    return <div>Affichage des commentaires !!! YEAHH</div>;
}

export default Comments;
