import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

import jwt_decode from "jwt-decode";

import DateTime from "../DateTime";

import { BiCheckShield } from "react-icons/bi";

function UserData(props) {
    const { realAuthor_id, dateTime, access } = props;
    const [realAuthor, setRealAuthor] = useState([]);
    const [dataOk, setDataOk] = useState(false);
    const [msg, setMsg] = useState("");
    const [errorMessage, setErrorMessage] = useState("");
    const [userFound, setUserFound] = useState(false);
    // const [notFound, setNotFound] = useState(true);

    const token = access;
    const decoded = jwt_decode(token);
    // const user_id = decoded.user_id;
    const user_role = decoded.role;

    const url = `http://localhost:8080/api/profiles/${realAuthor_id}`;
    const reqOptions = {
        method: "GET",
        headers: {
            Authorization: `Bearer ${token}`,
            Accept: "application/json",
            "Content-Type": "application/json",
        },
    };

    useEffect(() => {
        if (realAuthor_id === undefined || realAuthor_id === null) {
            setErrorMessage("Utilisateur inexistant");
        } else {
            const fetchUserData = async () => {
                try {
                    const res = await fetch(url, reqOptions);
                    const data = await res.json();

                    // console.log(data.MESSAGE);

                    // if (res.status === 201) {
                    //     setMessage("Compte supprimé");
                    //     setNotFound(false);
                    // }

                    // if (data.MESSAGE) {
                    //     setMessage(data.MESSAGE);
                    //     setNotFound(false);
                    // }

                    if (data.profile) {
                        setRealAuthor(data.profile);
                        setDataOk(true);
                        // setNotFound(false);
                    }
                } catch (err) {
                    if (err) {
                        setErrorMessage(err);
                    }
                }
            };

            fetchUserData();

            // fetch(url, reqOptions)
            //     .then((res) => {
            //         if (res.ok) {
            //             if (res.status === 201) {
            //                 setMessage("Compte supprimé");
            //                 setNotFound(false);
            //             }

            //             if (data.MESSAGE) {
            //                 setMessage(data.MESSAGE);
            //                 setNotFound(false);
            //             }
            //             if (data.profile) {
            //                 setRealAuthor(data.profile);
            //                 setDataOk(true);
            //                 setNotFound(false);
            //             } else {
            //                 setNotFound(true);
            //             }

            //             return res.json();
            //         } else {
            //             throw new Error("Utilisateur inexistant");
            //         }
            //     })
            //     .then((data) => {
            //         if (data.MESSAGE) {
            //             setMessage(data.MESSAGE);
            //             setNotFound(false);
            //         }
            //         if (data.profile) {
            //             setRealAuthor(data.profile);
            //             setDataOk(true);
            //             setNotFound(false);
            //         } else {
            //             setNotFound(true);
            //         }
            //     })
            //     .catch((err) => {
            //         if (err) {
            //             setMessage(err);
            //         }
            //     });
        }
    }, []);

    if (!dataOk) {
        return null;
    }

    const first_Name = realAuthor.firstName;
    const last_Name = realAuthor.lastName;
    const fullName = `${first_Name} ${last_Name}`;
    const initiales = fullName.match(/\b\w/g).join("").toUpperCase();

    // console.log(realAuthor);
    // console.log(notFound ? message : realAuthor.role === "admin" ? "Administrateur" : fullName);

    return (
        // <div>
        <div className="realAuthor">
            {/* <Link to={"/profile/" + realAuthor_id}> */}
            {realAuthor.role === "admin" ? (
                <div className="realAuthor__wrapper">
                    <div className="adminIcon">
                        <BiCheckShield className="icon" />
                    </div>

                    <div className="realAuthor__infos">
                        <p className="adminName">Administrateur</p>
                        {!dateTime ? null : (
                            <div className="realAuthor__infos--dateTime">
                                <DateTime datetime={dateTime} />
                            </div>
                        )}
                    </div>
                </div>
            ) : (
                <div className="realAuthor__wrapper">
                    {!realAuthor.avatarUrl ? (
                        <div className="realAuthor__avatar initiales">
                            <p>{initiales}</p>
                        </div>
                    ) : (
                        <img className="realAuthor__avatar" src={realAuthor.avatarUrl} alt="Photo de profile" />
                    )}
                    <div className="realAuthor__infos">
                        <p className="realAuthor__infos--fullName">{realAuthor ? "Compte supprimé" : fullName}</p>
                        {!dateTime ? null : (
                            <div className="realAuthor__infos--dateTime">
                                <DateTime datetime={dateTime} />
                            </div>
                        )}
                    </div>
                </div>
            )}
            {/* </Link> */}
        </div>
        // </div>
    );
}

export default UserData;
