import React, { Children, useEffect, useState } from "react";
// import { Link } from "react-router-dom";

// import jwt_decode from "jwt-decode";

import DateTime from "../DateTime";

export default function RealAuthor(props) {
    const { realAuthor_id, dateTime, access } = props;
    const [realAuthor, setRealAuthor] = useState([]);
    const [dataOk, setDataOk] = useState(false);
    const [message, setMessage] = useState("");
    const [userFound, setUserFound] = useState(false);

    // console.log(realAuthor_id);

    // const user = JSON.parse(sessionStorage.getItem("isAuthenticate"));
    // const token = user.pass;

    const token = access;
    // const decoded = jwt_decode(token);
    // const user_id = decoded.user_id;
    // const user_role = decoded.role;

    const url = `http://localhost:8080/api/profiles/${realAuthor_id}`;
    const reqOptions = {
        method: "GET",
        headers: {
            Authorization: `Bearer ${token}`,
            Accept: "application/json",
            "Content-Type": "application/json",
        },
    };

    // const fetchRealAuthor = async () => {
    //     // fetch(url, reqOptions)
    //     //     .then((res) => {
    //     //         if (!res.ok) {
    //     //             setMessage("Utilisateur inconnu");
    //     //         } else if (res.status !== 404) {
    //     //             setMessage("Utilisateur inconnu");
    //     //         } else {
    //     //             return res.json();
    //     //         }
    //     //     })
    //     //     .then((userData) => {
    //     //         setRealAuthor(userData);
    //     //         setDataOk(true);
    //     //     })
    //     //     .catch((err) => {
    //     //         setMessage(err);
    //     //     });

    //     try {
    //         const res = await fetch(url, reqOptions);
    //         const userData = await res.json();

    //         if (res.ok) {
    //             if (res.status === 404) {
    //                 setMessage("Utilisateur inconnu");
    //                 // console.log(realAuthor);
    //             } else {
    //                 setRealAuthor(userData);
    //                 setDataOk(true);
    //                 // console.log("L'utilisateur n'existe plus");
    //             }
    //         }
    //     } catch (err) {
    //         setMessage(err);
    //     }
    // };

    useEffect(() => {
        if (realAuthor_id === undefined || realAuthor_id === null) {
            setMessage("Utilisateur inconnu");
            // console.log("utilisateur inconnu");
        } else {
            fetch(url, reqOptions)
                .then((res) => {
                    // if (res.status === 404) {
                    //     setUserFound(false);
                    //     console.log(res.status);
                    //     console.log(userFound);
                    // } else {
                    //     setUserFound(true);
                    //     console.log(res.status);
                    //     console.log(userFound);
                    // }
                    if (res.ok) {
                        setUserFound(true);
                        // console.log(res.status);
                        return res.json();
                    } else {
                        if (res.status === 404) {
                            setMessage("Utilisateur inexistant");
                        }
                        throw new Error("Something went wrong");
                    }
                })
                .then((data) => {
                    setRealAuthor(data.profile);
                    setDataOk(true);
                })
                .catch((err) => {
                    if (err) {
                        setMessage(err);
                    }
                });
        }

        // if (realAuthor_id !== undefined) {
        //     fetchRealAuthor();
        // } else {
        //     setMessage("Utilisateur inconnu");
        // }
        // console.log(message);
    }, []);

    if (!dataOk) {
        return null;
    }

    console.log("", message);
    // console.log(realAuthor);
    const first_Name = realAuthor.firstName;
    const last_Name = realAuthor.lastName;
    const fullName = `${first_Name} ${last_Name}`;
    const initiales = fullName.match(/\b\w/g).join("").toUpperCase();

    // console.log(first_Name.length > 0 ? "il y a un utilisateur" : "Il n'y a pas d'utilisateur");

    return (
        <div>
            <div className="realAuthor">
                {/* <p>Article créé par</p> */}
                {!realAuthor.firstName ? (
                    <div className="realAuthor__wrapper">
                        <div className="realAuthor__infos">
                            <p className="realAuthor__infos--fullName">{message}</p>
                            <div className="realAuthor__infos--dateTime">
                                <p>posté </p>
                                <DateTime datetime={dateTime} />
                            </div>
                        </div>
                    </div>
                ) : (
                    <div className="realAuthor__wrapper">
                        {!realAuthor.avatarUrl ? (
                            <div className="realAuthor__avatar initiales">
                                <p>{initiales}</p>
                            </div>
                        ) : (
                            <img className="realAuthor__avatar" src={realAuthor.avatarUrl} alt="Photo de profile" />
                        )}
                        <div className="realAuthor__infos">
                            <p className="realAuthor__infos--fullName">
                                {realAuthor.firstName && realAuthor.lastName
                                    ? first_Name + " " + last_Name
                                    : "Utilisateur inconnu"}
                            </p>
                            <div className="realAuthor__infos--dateTime">
                                <p>posté </p>
                                <DateTime datetime={dateTime} />
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}
