import React, { useEffect, useState, Component } from "react";
import { Link } from "react-router-dom";

export default class RealAuthor extends Component {
    constructor(props) {
        super(props);

        // const { realAuthor_id } = this.props.realAuthor_id;

        this.state = {
            userInfo: [],
            DataIsLoaded: false,
        };
    }

    async componentDidMount() {
        const user = JSON.parse(sessionStorage.getItem("isAuthenticate"));
        const token = user.pass;
        const userId = user.id;

        const url = `http://localhost:8080/api/profiles/${this.props.realAuthor_id}`;

        const reqOptions = {
            method: "GET",
            headers: {
                Authorization: `Bearer ${token}`,
                Accept: "application/json",
                "Content-Type": "application/json",
            },
        };

        try {
            const res = await fetch(url, reqOptions);
            const userData = await res.json();
            this.setState({ userInfo: userData, DataIsLoaded: true });
            console.log(this.state);
        } catch (err) {
            console.log(err);
        }
    }

    render() {
        const { DataIsLoaded, userInfo } = this.state;

