import React, { useState, useEffect } from "react";

function DeletePost(props) {
    // On récupère l'id du post à supprimer
    const { post_id, author_id } = props;

    const [isAuthorized, setIsAuthorized] = useState(false);

    // On récupère les données pour les authorisations
    const user = JSON.parse(sessionStorage.getItem("isAuthenticate"));
    const token = user.pass;
    const user_id = user.id;
    const user_role = user.role;
    const role = "admin";

    // On configure la requête
    const url = `http://localhost:8080/api/articles/${post_id}`;
    const reqOptions = {
        method: "DELETE",
        headers: {
            Authorization: `Bearer ${token}`,
            Accept: "application/json",
            "Content-Type": "application/json",
        },
    };

    // On vérifi si l'utilisateur est autorisé à supprimer le commentaire
    const authorization = async () => {
        if (user_id === author_id || user_role === role) {
            setIsAuthorized(true);
            console.log(isAuthorized);
        }
    };

    useEffect(() => {
        authorization();
    }, []);

    const deletePost = async (e) => {
        e.preventDefault();
        console.log("click");
        try {
            let res = await fetch(url, reqOptions);
            let postDel = await res.json();

            console.log("=====> Réponse commentaire envoyé : ");
            console.log(postDel);

            window.location.reload();
        } catch (err) {
            console.log(err);
        }

        // fetch(url, reqOptions)
        //     .then((res) => {
        //         if (res.ok) {
        //             return res.json();
        //         }
        //     })
        //     .then((data) => {
        //         console.log(data);
        //     })
        //     .catch((err) => {
        //         console.log(err);
        //     });
    };

    return (
        <div>
            {!isAuthorized ? null : (
                <button className="post__delete btn btn__delete" onClick={(e) => deletePost(e)}>
                    Supprimer
                </button>
            )}
        </div>
    );
}

export default DeletePost;
