import React, { useEffect, useState, Component } from "react";
import { Link } from "react-router-dom";

export default function RealAuthor(props) {
    const { realAuthor_id } = props;
    const [realAuthor, setRealAuthor] = useState({ realAuthor: {} });
    const [dataOk, setDataOk] = useState(false);

    console.log(realAuthor_id);

    const user = JSON.parse(sessionStorage.getItem("isAuthenticate"));
    const token = user.pass;

    const url = `http://localhost:8080/api/profiles/${realAuthor_id}`;
    const reqOptions = {
        method: "GET",
        headers: {
            Authorization: `Bearer ${token}`,
            Accept: "application/json",
            "Content-Type": "application/json",
        },
    };

    const fetchRealAuthor = async () => {
        try {
            const res = await fetch(url, reqOptions);
            const userData = await res.json();

            setRealAuthor({ realAuthor: userData });
            setDataOk(true);

            if (!res.ok) {
                console.log("Utilisateur non trouvé");
            } else {
                console.log(realAuthor);
            }
        } catch (err) {
            console.log(err);
        }
    };

    useEffect(() => {
        if (realAuthor_id !== undefined) {
            fetchRealAuthor();
        }
    }, []);

    // const first_Name = realAuthor.profile.firstName;
    // const last_Name = realAuthor.profile.lastName;
    // const fullName = `${first_Name} ${last_Name}`;
    // const initiales = fullName.match(/\b\w/g).join("").toUpperCase();
    return (
        <div>
            {dataOk ? (
                <div className="realAuthor">
                    {!realAuthor.profile.avatarUrl ? (
                        <div className="realAuthor__avatar initiales">{/* <p>{initiales}</p> */}</div>
                    ) : (
                        <img
                            className="realAuthor__avatar"
                            src={realAuthor.profile.avatar_Url}
                            alt="Photo de profile"
                        />
                    )}

                    <div className="realAuthor__fullName">
                        <p>
                            {realAuthor.profile.firstName} {realAuthor.profile.profile.lastName}
                        </p>
                    </div>
                </div>
            ) : null}
        </div>
    );
}

