// import logo from "./logo.svg";
// import "./sass/App.scss";
import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import { Navigation, Footer, Login, Home, User_profile } from "./webpages";

function App() {
    // const [isLoggedIn, setIsLoggedIn] = useState(false);
    // const [token, setToken] = useState("");
    // const [userId, setUserId] = useState("");

    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [msg, setMsg] = useState("");
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [token, setToken] = useState("");
    const [userId, setUserId] = useState("");

    const [user, setUser] = useState(null);
    const handleLogin = () => setUser({ user_id: userId });
    const handleLogout = () => setUser(null);
    // const history = useHistory();
    const url = `http://localhost:8080/api/auth/login`;

    const userObject = {
        email: email,
        password: password,
    };

    const postLoggedIn = {
        method: "POST",
        body: JSON.stringify(userObject),
        headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
        },
    };

    const Auth = async (e) => {
        e.preventDefault();

        try {
            let res = await fetch(url, postLoggedIn);

            // const data = await res.json();

            // const dataString = JSON.stringify(data)

            if (res.status == 404) {
                console.log(res.status);
                setIsLoggedIn(false);
                setMsg("Cet utilisateur n'existe pas !");
            }

            const data = await res.json();
            console.log(data);

            if (res.json) {
                // console.log("y'a une réponse !");

                setToken(data.token);
                setUserId(data.userId);
                setIsLoggedIn(true);
            }
        } catch (error) {
            if (error.res) {
                setMsg(error.res.data.msg);
            }
        }
    };

    // icon hide and show password

    const [passwordShown, setPasswordShown] = useState(false);
    const [show, setShow] = useState(false);

    const togglePassword = () => {
        setPasswordShown(!passwordShown);
        setShow(!passwordShown);
    };

    // const toggleEyes = () => {
    //     setShow(!passwordShown);
    // };

    // const [user, setUser] = useState(null);

    console.log("=====> User :");
    console.log(user);

    return (
        <div className="App">
            <div className="App__bgColor"></div>
            <Router>
                {user ? console.log("user loggedIn") : <Login />}
                <header className="App-header">
                    <Navigation user={user} />
                </header>
                <Routes>
                    <Route path="/Home" element={<Home user={user} />} />
                    <Route path="/User_profile" element={<User_profile user={user} />} />
                </Routes>
                <Footer />
            </Router>
        </div>
        // <div className="App">
        //     <header className="App-header">
        //         <img src={logo} className="App-logo" alt="logo" />
        //         <h1>Bonjour !</h1>
        //     </header>
        // </div>
    );
}

export default App;
