import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Route } from "react-router-dom";

function Post() {
    let { postSlug } = useParams();
    const [post, setPost] = useState({ post: {} });
    const [dataIsLoaded, setDataIsLoaded] = useState(false);

    console.log("=====> POST SLUG");
    console.log(postSlug);

    // Token de l'utilisateur
    const user = JSON.parse(sessionStorage.getItem("isAuthenticate"));
    const token = user.pass;

    // Request options
    const url = `http://localhost:8080/api/articles/${postSlug}`;
    const reqOptions = {
        method: "GET",
        headers: {
            Authorization: `Bearer ${token}`,
            Accept: "application/json",
            "Content-Type": "application/json",
        },
    };

    const fetchDetails = () => {
        fetch(url, reqOptions)
            .then((res) => res.json())
            .then((data) => {
                setPost(data);
                setDataIsLoaded(true);
            });
    };

    // const getPostDetails = async () => {
    //     try {
    //         const res = await fetch(url, reqOptions);
    //         const postData = await res.json();
    //         // this.setState({ items: postsData.articlesFound, DataisLoaded: true });
    //         setPost(postData);
    //         console.log(postData.articleFound.id);
    //         // console.log(post);
    //         // console.log(this.state);
    //     } catch (err) {
    //         console.log(err);
    //     }
    // };

    useEffect(() => {
        fetchDetails();
        // getPostDetails();
    }, []);

    console.log("=====> PostDetails results");
    console.log(post);

    if (!dataIsLoaded) {
        return (
            <div>
                <h1> Pleses wait some time.... </h1>
            </div>
        );
    }

    return (
        <div className="post">
            <div className="post__container container">
                <div className="post__header">
                    <div className="post__header--author">
                        {!post.articleFound.author_avatar ? (
                            <div className="author__img initiales">
                                <p>
                                    {`${post.articleFound.author_firstName} ${post.articleFound.author_lastName}
                                                `
                                        .match(/\b\w/g)
                                        .join("")
                                        .toUpperCase()}
                                </p>
                            </div>
                        ) : (
                            <img className="author__img" src={post.articleFound.author_avatar} />
                        )}
                    </div>
                    {post.articleFound.id}
                    {post.articleFound.title}
                </div>
            </div>
        </div>
    );
}

export default Post;
