import React, { useState } from "react";
import { Routes, Route, Link, Navigate } from "react-router-dom";

class Home extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            items: [],
            DataisLoaded: false,
        };
    }

    async componentDidMount() {
        const user = JSON.parse(sessionStorage.getItem("isAuthenticate"));
        console.log(user.pass);

        const url = `http://localhost:8080/api/articles`;

        const token = user.pass;
        const reqOptions = {
            method: "GET",
            headers: {
                Authorization: `Bearer ${token}`,
                Accept: "application/json",
                "Content-Type": "application/json",
            },
        };

        try {
            const res = await fetch(url, reqOptions);
            const postsData = await res.json();
            this.setState({ items: postsData.articlesFound, DataisLoaded: true });
            console.log(this.state);
        } catch (err) {
            console.log(err);
        }
    }

    render() {
        // ====================> Like Request
        const handleLike = async (e) => {
            const user = JSON.parse(sessionStorage.getItem("isAuthenticate"));
            console.log(user.pass);

            const url = `http://localhost:8080/api/articles/${e}/like`;

            const like = {
                like: 1,
            };

            const token = user.pass;
            const reqOptions = {
                method: "POST",
                body: JSON.stringify(like),
                headers: {
                    Authorization: `Bearer ${token}`,
                    Accept: "application/json",
                    "Content-Type": "application/json",
                },
            };

            try {
                const res = await fetch(url, reqOptions);
                const resLiked = await res.json();

                console.log(resLiked);
            } catch (err) {
                console.log(err);
            }
        };

        // =================> Show all posts
        const { DataisLoaded, items } = this.state;

        if (!DataisLoaded)
            return (
                <div>
                    <h1> Pleses wait some time.... </h1>{" "}
                </div>
            );

        return (
            <div className="home">
                <div className="container">
                    <h1 hidden>Home</h1>
                    <div className="home__articles">
                        {/* Boucle pour afficher tous les articles trouvÃ© */}
                        {items.map((item) => (
                            <div className="home__article" key={item.article.id}>
                                <div className="article__img">
                                    <img src={item.article.images} />
                                </div>
                                <div className="home__article--title">{item.article.title}</div>
                                <div className="home__article--author">
                                    <img src={item.article.author_avatar} />
                                    {item.article.author_firstName + " " + item.article.author_lastName}
                                </div>
                                <div className="home__article--content">{item.article.content}</div>
                                <div className="home__article--social">
                                    {/* <button onClick={handleLike(item.article.id)}>Likes : </button> */}

                                    <p>
                                        <button onClick={(e) => handleLike(item.article.id)}>Likes : </button>
                                        {item.article.likes}
                                    </p>
                                    <p>Dislikes : {item.article.dislikes}</p>
                                    <p>Shares : {item.article.shares}</p>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        );
    }
}

export default Home;
