import React, { useState, useEffect } from "react";

function UsersProfile() {
    const [users, setUsers] = useState();
    const [dataIsLoaded, setDataIsLoaded] = useState(false);

    const user = JSON.parse(sessionStorage.getItem("isAuthenticate"));
    const token = user.pass;

    const url = `http://localhost:8080/api/profiles`;
    const reqOptions = {
        method: "GET",
        headers: {
            Authorization: `Bearer ${token}`,
            Accept: "application/json",
            "Content-Type": "application/json",
        },
    };

    const fetchUsers = () => {
        fetch(url, reqOptions)
            .then((res) => {
                if (res.ok) {
                    return res.json();
                }
            })
            .then((data) => {
                console.log(data);

                setUsers(data);
                setDataIsLoaded(true);
            });
    };

    useEffect(() => {
        fetchUsers();
    }, []);

    const usersList = users.map((user) => {
        <div key={user.usersProfile.user_Id}>Hello</div>;
    });

    if (!dataIsLoaded) {
        return (
            <div>
                <h1> Pleases wait some time.... </h1>
            </div>
        );
    }

    return users.forEach((user) => {
        <div key={user.usersProfile.user_Id}>
            Hello
            <div>{user.usersProfile.role}</div>
        </div>;
    });
    // return (
    //     <div>
    //         UsersProfile coding en cours
    //         <div>{usersList}</div>
    //     </div>
    // );
}

export default UsersProfile;
