import React, { useState, useEffect } from "react";

function DateTime(props) {
    const { datetime } = props;

    const [timeAfter, setTimeAfter] = useState(" ");

    // const get_date = new Date(datetime).getDate();
    // const get_month = new Date(datetime).getMonth() + 1; // On ajoute +1 afin de compenser la perte de 1 moi durant la convertion
    // const get_year = new Date(datetime).getFullYear();

    // const date = `${get_year}, ${get_month}, ${get_date}`;
    const now = new Date();
    const date = new Date(datetime);
    // const timeBetween = now - date;

    const unAutreTest = now - date;
    const jours = unAutreTest / (1000 * 3600 * 24);

    console.log(jours);
    // console.log(now);

    // const test = now - otherDate;
    // console.log(test.toString());

    // const time = new Date(test);
    // console.log(time);

    var diff = {}; // Initialisation du retour

    function dateDiff(date1, date2) {
        var tmp = date2 - date1;

        tmp = Math.floor(tmp / 1000); // Nombre de secondes entre les 2 dates
        diff.sec = tmp % 60; // Extraction du nombre de secondes

        tmp = Math.floor((tmp - diff.sec) / 60); // Nombre de minutes (partie entière)
        diff.min = tmp % 60; // Extraction du nombre de minutes

        tmp = Math.floor((tmp - diff.min) / 60); // Nombre d'heures (entières)
        diff.hour = tmp % 24; // Extraction du nombre d'heures

        tmp = Math.floor((tmp - diff.hour) / 24); // Nombre de jours restants
        diff.day = tmp;

        return diff;
    }

    diff = dateDiff(date, now);

    // console.log(`Créé il y a ${diff.day} jours, ${diff.hour} heures, ${diff.sec} secondes`);

    function set_time() {
        if (diff.day <= 0 && diff.hour <= 0 && diff.min <= 0 && diff.sec > 0) {
            setTimeAfter(`${diff.sec} sec`);
        } else if (diff.day <= 0 && diff.hour <= 0 && diff.min > 0) {
            setTimeAfter(`${diff.min} min`);
        } else if (diff.day <= 0 && diff.hour > 0) {
            setTimeAfter(`${diff.hour} h`);
        } else if (diff.day > 0) {
            setTimeAfter(`${diff.day} jours`);
        }
    }

    useEffect(() => {
        set_time();
    }, []);

    return (
        <div>
            Créer il y a {timeAfter}
            {/* Créé il y a {diff.day} jours, {diff.hour} heures, {diff.sec} secondes */}
        </div>
    );
}

export default DateTime;
