import React, { useState, useEffect } from "react";
// import { useCookies, cookies, withCookies } from "react-cookie";
import jwt_decode from "jwt-decode";

import UserData from "./usersProfile/UserData";

function UserLoggedIn(props) {
    const { access } = props;

    const [user, setUser] = useState({ user: {} });

    const decoded = jwt_decode(access);
    const user_id = decoded.userId;

    const url = `http://localhost:8080/api/profiles/${user_id}`;

    const reqOptions = {
        method: "GET",
        headers: {
            Authorization: `Bearer ${access}`,
            Accept: "application/json",
            "Content-Type": "application/json",
        },
    };

    useEffect(() => {
        async function fetchData() {
            const res = await fetch(url, reqOptions);
            const data = await res.json();

            setUser({ user: data, DataIsLoaded: true });

            console.log(data);
        }

        fetchData();
    }, []);

    return (
        <div className="userLoggedIn">
            <UserData realAuthor_id={user.user_id} access={access} />
        </div>
    );
}

export default UserLoggedIn;
