import React, { Component } from "react";

export class Post extends Component {
    constructor(props) {
        super(props);

        this.state = {
            post: [],
            DataIsLoaded: false,
        };
    }
    render() {
        const { DataIsLoaded, post } = this.state;

        if (!DataIsLoaded)
            return (
                <div>
                    <h1> Pleses wait some time.... </h1>{" "}
                </div>
            );

        return <div>Post</div>;
    }
}

export default Post;

