import React, { Component } from "react";

export default class Comments extends Component {
    constructor(props) {
        super(props);

        this.state = {
            comments: [],
            DataIsLoaded: false,
        };
    }

    render() {
        const { DataIsLoaded, comments } = this.state;

        if (!DataIsLoaded) {
            return (
                <div>
                    <h1> Pleses wait some time.... </h1>
                </div>
            );
        }

        return <div>Comments</div>;
    }
}
