import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Route } from "react-router-dom";

import { BiLike, BiDislike, BiShare, BiComment, BiCommentAdd } from "react-icons/bi";

function Post() {
    let { postSlug } = useParams();
    const [post, setPost] = useState({ post: {} });
    const [dataIsLoaded, setDataIsLoaded] = useState(false);

    console.log("=====> POST SLUG");
    console.log(postSlug);

    // Token de l'utilisateur
    const user = JSON.parse(sessionStorage.getItem("isAuthenticate"));
    const token = user.pass;

    // Request options
    const url = `http://localhost:8080/api/articles/${postSlug}`;
    const reqOptions = {
        method: "GET",
        headers: {
            Authorization: `Bearer ${token}`,
            Accept: "application/json",
            "Content-Type": "application/json",
        },
    };

    const fetchDetails = () => {
        fetch(url, reqOptions)
            .then((res) => res.json())
            .then((data) => {
                setPost(data);
                setDataIsLoaded(true);
            });
    };

    useEffect(() => {
        fetchDetails();
    }, []);

    console.log("=====> PostDetails results");
    console.log(post);

    let date1 = Date();
    let date2 = Date.now();

    const datetime = new Date().toJSON().slice(0, 19).replace("T", " ");

    console.log(date2);
    console.log(parseInt(post.articleFound.timestamp));
    console.log(parseInt(datetime));

    // post.articleFound.timestamp

    // Date.getTime();
    // console.log(Date.getTime);

    // function dateJs(date1, date2) {
    //     Date.getTime();

    //     return;
    // }

    if (!dataIsLoaded) {
        return (
            <div>
                <h1> Pleses wait some time.... </h1>
            </div>
        );
    }

    return (
        <div className="post">
            <div className="post__container container">
                <div className="post__details">
                    <div className="post__header">
                        <div className="post__header--author">
                            {!post.articleFound.author_avatar ? (
                                <div className="author__img initiales">
                                    <p>
                                        {`${post.articleFound.author_firstName} ${post.articleFound.author_lastName}
                                                `
                                            .match(/\b\w/g)
                                            .join("")
                                            .toUpperCase()}
                                    </p>
                                </div>
                            ) : (
                                <div className="author__img--wrapper">
                                    <img className="author__img" src={post.articleFound.author_avatar} />
                                </div>
                            )}

                            <p className="author__name">
                                {post.articleFound.author_firstName + " " + post.articleFound.author_lastName}
                            </p>
                            <div className="post__header--datetime">{post.articleFound.timestamp}</div>
                        </div>

                        <div className="post__header--details">
                            <h2 className="post__title">{post.articleFound.title}</h2>

                            {/* <p className="author__name">
                                {post.articleFound.author_firstName + " " + post.articleFound.author_lastName}
                            </p>
                            <div className="post__header--datetime">{post.articleFound.timestamp}</div> */}
                        </div>
                        {/* {post.articleFound.id} */}
                    </div>

                    {post.articleFound.img ? (
                        <div className="post__content">
                            <p>{post.articleFound.content}</p>
                            <img
                                className="post__content--img"
                                src={post.articleFound.img}
                                alt={"image de l'article" + post.articleFound.title}
                            />
                        </div>
                    ) : (
                        <div className="post__content">
                            <p>{post.articleFound.content}</p>
                        </div>
                    )}

                    <div className="post__footer">
                        <p className="social">
                            <span className="social__icon">
                                <BiLike />
                            </span>
                            <span className="social__count">{post.articleFound.likes}</span>
                        </p>
                        <p className="social">
                            <span className="social__icon">
                                <BiDislike />
                            </span>
                            <span className="social__count"> {post.articleFound.dislikes}</span>
                        </p>
                        <p className="social">
                            <span className="social__icon">
                                <BiShare />
                            </span>
                            <span className="social__count">{post.articleFound.shares}</span>
                        </p>
                        <p className="social">
                            <span className="social__icon">
                                <BiComment />
                            </span>
                            <span className="social__count">{post.articleFound.comments}</span>
                        </p>
                        <button className="social__icon btn">
                            <BiCommentAdd />
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Post;
