// import logo from "./logo.svg";
// import "./sass/App.scss";
import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import { Navigation, Footer, Login, Home, User_profile } from "./webpages";

// Tutoriel Private Routes react
// import Loader from "shareComponent/Loader";
import ProtectedRoutes from "./components/ProtectedRoute"; //Authenticated routes
import PublicRoute from "./components/PublicRoute";
import PrivateRoute from "./components/PrivateRoute";

const LoginPage = lazy(() => import("./webpages/Login"));
// const Register = lazy(() => import("components/Register"));
// const ForgotPassword = lazy(() => import("components/ForgotPassword"));
// const NoFoundComponent = lazy(() => import("components/NoFoundComponent"));

function App() {
    let isLoggedIn = localStorage.getItem("user_id");
    // console.log(isLoggedIn);

    const UserContext = React.createContext("user_logged_in");

    if (!isLoggedIn) {
        return (
            <div className="App">
                <div className="App__bgColor"></div>
                <Router>
                    <Login />
                </Router>
            </div>
        );
    }
    return (
        // <UserContext.Provider value="user_not_logged_in"></UserContext.Provider>
        <div className="App">
            <div className="App__bgColor"></div>
            <Router>
                {/* <Login /> */}
                <header className="App-header">
                    <Navigation isLoggedIn={isLoggedIn} />
                </header>
                <Routes>
                    <Route path="/Home" element={<Home isLoggedIn={isLoggedIn} />} />
                    <Route path="/User_profile" element={<User_profile isLoggedIn={isLoggedIn} />} />
                </Routes>
                <Footer />
            </Router>
        </div>
    );
}

export default App;
