import React, { useState } from "react";

function Comments(props) {
    const { post_id } = props;

    const [data, setData] = useState({ data: [] });
    // const [dataIsLoaded, setDataIsLoaded] = useState(false);
    const [isLoading, setIsLoading] = useState(false);
    const [err, setErr] = useState("");

    const user = JSON.parse(sessionStorage.getItem("isAuthenticate"));
    const token = user.pass;

    const url = `http://localhost:8080/api/articles/${post_id}/comments`;
    const reqOptions = {
        method: "GET",
        headers: {
            Authorization: `Bearer ${token}`,
            Accept: "application/json",
            "Content-Type": "application/json",
        },
    };

    const handleClick = async () => {
        setIsLoading(true);

        try {
            const res = await fetch(url, reqOptions);

            if (!res.ok) {
                throw new Error(`Error! status: ${res.status}`);
            }

            const result = await res.json();

            console.log("result is: ", result);
            console.log("result de data.allComments: ", data.allComments);
            // console.log("Résultat de data.allComments[0].id: ", data.allComments[0].id);
            // console.log("result is: ", JSON.stringify(result, null, 4));

            setData(result);
        } catch (err) {
            setErr(err.message);
        } finally {
            setIsLoading(false);
        }
    };

    console.log(data);

    // const showComments = data.allComments.map((comment) => {
    //     return (
    //         <div key={comment.id}>
    //             <p>{comment.comment}</p>
    //         </div>
    //     );
    // });

    return (
        <div>
            {err && <h2>{err}</h2>}
            <button onClick={handleClick}>Fetch data</button>
            {isLoading && <h2>Loading...</h2>}

            {data.allComments?.map((comment) => {
                return (
                    <div key={comment.id}>
                        {/* <h2>{person.email}</h2>
                    <h2>{person.first_name}</h2>
                    <h2>{person.last_name}</h2>
                    <br /> */}
                    </div>
                );
            })}
        </div>
    );
}

export default Comments;
