                    <PrivateRoute path="/" isAuthenticated={isAuthenticated}>
                        <ProtectedRoutes />
                    </PrivateRoute>
                    <Route path="*">
                        <NoFoundComponent />
                    </Route>
                </Switch>
            </Suspense>
        </Router>
    );
};

export default App;
