import React from "react";
// import React, { useState, useEffect } from "react";

export default class Comments extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            comments: [],
            message: "",
            DataIsLoaded: false,
        };

        const handleClick = (e) => {
            const user = JSON.parse(sessionStorage.getItem("isAuthenticate"));
            const token = user.pass;

            // ID du post
            // console.log(this.props.post_id);

            const url = `http://localhost:8080/api/articles/${this.props.post_id}/comments`;
            const reqOptions = {
                method: "GET",
                headers: {
                    Authorization: `Bearer ${token}`,
                    Accept: "application/json",
                    "Content-Type": "application/json",
                },
            };

            fetch(url, reqOptions)
                .then((res) => {
                    if (res.ok) {
                        return res.json();
                    }
                })
                .then((data) => {
                    console.log(data);
                    return handleClick();
                });
        };
    }

    async componentDidMount() {
        const user = JSON.parse(sessionStorage.getItem("isAuthenticate"));
        const token = user.pass;

        // ID du post
        // console.log(this.props.post_id);

        const url = `http://localhost:8080/api/articles/${this.props.post_id}/comments`;
        const reqOptions = {
            method: "GET",
            headers: {
                Authorization: `Bearer ${token}`,
                Accept: "application/json",
                "Content-Type": "application/json",
            },
        };

        try {
            const res = await fetch(url, reqOptions);
            const data = await res.json();

            // console.log(data);

            if (data.allComments) {
                // console.log(data.allComments);
                this.setState({ comments: data.allComments, message: "Commentaires chargé", DataIsLoaded: true });
            }
            console.log(this.state);

            // this.setState({ comments: data.allComments, DataIsLoaded: true });
            // console.log(this.state);
            // console.log(data);
        } catch (err) {
            console.log(err);
        }
    }

    render() {
        const { DataIsLoaded, comments, message } = this.state;

        if (!DataIsLoaded)
            return (
                <div>
                    <h1> {message} </h1>
                </div>
            );

        // console.log(comments);

        // const showComments = comments.map((comment) => {
        //     // return <div key={comment.id}>HELLO</div>;
        // });

        return <div>{!DataIsLoaded ? "Pas de commentaires" : message}</div>;
    }
}
