import React, { useState } from "react";
import logo from "../images/icon.svg";
// tutorial
import { useHistory } from "react-router-dom";

// Import des icons de la page login
import { BiLogInCircle, BiLockOpenAlt, BiShow, BiHide, BiCycling } from "react-icons/bi";

// class Login extends React.Component {
function Login() {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [msg, setMsg] = useState("");
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    // const history = useHistory();
    const url = `http://localhost:8080/api/auth/login`;

    const userObject = {
        email: email,
        password: password,
    };

    const postLoggedIn = {
        method: "POST",
        body: JSON.stringify(userObject),
        headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
        },
    };

    const Auth = async (e) => {
        e.preventDefault();

        try {
            let res = await fetch(url, postLoggedIn);

            // const data = await res.json();

            // const dataString = JSON.stringify(data)

            if (res.status == 404) {
                console.log(res.status);
                return setMsg("Cet utilisateur n'existe pas !");
            }

            const data = await res.json();
            console.log(data.userId);
            console.log(res);

            setIsLoggedIn(true);

            if (isLoggedIn) {
                console.log("Connexion réussis, ajouter la redirection vers la page d'accueil");
            } else {
                console.log("Connexion echouée");
            }
        } catch (error) {
            if (error.res) {
                setMsg(error.res.data.msg);
            }
        }
    };

    // icon hide and show password

    const [passwordShown, setPasswordShown] = useState(false);
    const [show, setShow] = useState(false);

    const togglePassword = () => {
        setPasswordShown(!passwordShown);
        setShow(!passwordShown);
    };

    // const toggleEyes = () => {
    //     setShow(!passwordShown);
    // };

    return (
        <div className="login">
            <div className="login__container">
                <div className="login__branding">
                    <img src={logo} alt="Logo Groupomania" />
                    <h1 className="login__title">Groupomania</h1>
                </div>

                <div className="login__links">
                    <ul className="login__links--items">
                        <li className="item">Connexion</li>
                        <p className="item">|</p>
                        <li className="item">S'inscrire</li>
                    </ul>
                </div>

                <form onSubmit={Auth} className="login__form">
                    <div className="login__input-edit">
                        <div>
                            <label className="login__label">
                                <div className="login__icon">
                                    <BiLogInCircle />
                                </div>
                                <input
                                    className="login__input-text"
                                    type="text"
                                    name="email"
                                    placeholder="email"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                />
                            </label>
                        </div>

                        <div>
                            <label className="login__label">
                                <div className="login__icon">
                                    <BiLockOpenAlt />
                                </div>

                                <div className="login__icon--hideShow">
                                    <span
                                        className="show"
                                        onClick={togglePassword}
                                        style={{ display: show ? "none" : "block" }}
                                    >
                                        <BiShow />
                                    </span>
                                    <span
                                        className="hide"
                                        onClick={togglePassword}
                                        style={{ display: show ? "block" : "none" }}
                                    >
                                        <BiHide />
                                    </span>
                                </div>
                                <input
                                    className="login__input-text"
                                    // type="password"
                                    type={passwordShown ? "text" : "password"}
                                    name="password"
                                    placeholder="******"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                />
                            </label>
                        </div>
                    </div>
                    {/* <div>
                    <label className="login__rememberMe">
                        <input className="login__rememberMe--checkbox" type="checkbox" id="scales" name="scales" />
                        <p className="login__rememberMe--text">Se souvenir de moi</p>
                    </label>
                </div> */}

                    <p>{msg}</p>
                    <input className="login__btn btn" type="submit" value="Connexion" />
                    {/* <p>
                    <li>Mot de passe oublié ?</li>
                </p> */}
                </form>
            </div>
        </div>
    );
}

export default Login;
