import React, { useState } from "react";
// import { Routes, Route, Link, Navigate, Outlet } from "react-router-dom";

import Posts from "./articles/Posts";
import PostForm from "../components/PostForm";
import UsersProfile from "../components/UsersProfile";
// import ErrorBoundary from "../components/ErrorBoundary";

import { BiPlus, BiX } from "react-icons/bi";

function Home() {
    const [createMod, setCreateMod] = useState(false);

    function toggleCreateMod(e) {
        setCreateMod(!createMod);
    }
    return (
        <div className="home">
            <div className="home__container container">
                <div className="home__article">
                    <h1 hidden>Home</h1>

                    <div>
                        <button
                            type="button"
                            className="home__article--createPost btn"
                            onClick={toggleCreateMod}
                            style={{ display: createMod ? "none" : "block" }}
                        >
                            <span className="createMod__icon">
                                <BiPlus />
                            </span>

                            {/* <p>Ajouter un nouvel article</p> */}
                        </button>

                        <div className={createMod ? "createPost__show" : "createPost__hide"}>
                            <button type="button" className="btn" onClick={toggleCreateMod}>
                                <span
                                    className="createMod__close icon"
                                    style={{ display: createMod ? "block" : "none" }}
                                >
                                    <BiX />
                                </span>
                            </button>
                            <PostForm />
                        </div>
                    </div>

                    <div className="home__articles">
                        <Posts />
                    </div>
                </div>
                <UsersProfile />
            </div>
        </div>
    );
}

export default Home;
