import React, { useState } from "react";

import { BiShare } from "react-icons/bi";

function SharePost(props) {
    const { post_id, userId } = props;
    const [shareNumber, setShareNumber] = useState(1);

    const [message, setMessage] = useState([]);

    const [shared, setShared] = useState(false);

    const user = JSON.parse(sessionStorage.getItem("isAuthenticate"));
    const token = user.pass;
    const user_id = user.id;

    const shareObject = {
        share: shareNumber,
    };

    const url = `http://localhost:8080/api/articles/${post_id}/share`;
    const reqOptions = {
        method: "POST",
        body: JSON.stringify(shareObject),
        headers: {
            Authorization: `Bearer ${token}`,
            Accept: "application/json",
            "Content-Type": "application/json",
        },
    };

    function shareButton(e) {
        // setShared(true)
        setShareNumber(1);

        fetch(url, reqOptions)
            .then((res) => {
                if (res.ok) {
                    return res.json();
                }
            })
            .then((data) => {
                console.log(data);
                setMessage(data);
            })
            .catch((err) => {
                setMessage("Une erreur est survenue ! " + err);
            });
    }

    console.log(JSON.stringify(message));

    // const showMessage = message.map((msg) => {
    //     <span className="share__message">{msg}</span>;
    // });
    /*
    Si l'utilisateur n'a pas encore partagé
      shareNumber = 1
    Sinon
      shareNumber = 0
    
    */

    return (
        <div>
            <button onClick={(e) => shareButton()}>
                <BiShare />
            </button>

            {/* <div>{showMessage}</div> */}
        </div>
    );
}

export default SharePost;
